function Ebola.AntiScreenShot.block()
	if(Ebola.AntiScreenShot.CVAR.Enabled:GetBool()) then
		_G.render.Capture = function(dat)
			if(dat.format == "jpeg") then
				return missingpng;
			elseif(dat.format == "png") then
				return missingpng;
			end
		end

		util.Base64Encode = function(string)
			local kidding = encode(missingpng);

			return fake;
		end

		-- AL.Capture = function(dat)
		-- 	if(dat.format == "jpeg") then
		-- 		return missingpng;
		-- 	elseif(dat.format == "png") then
		-- 		return missingpng;
		-- 	end
		-- end


		-- AL.Base64 = function(string)
		-- 	local kidding = encode(missingpng);

		-- 	return fake;
		-- end
	else
		_G.render.Capture = capture;
		util.Base64Encode = encode;
	end
end

Ebola.AntiScreenShot.block();
cvars.AddChangeCallback(Ebola.Global.Properties.CVARPref .. "_Antiscreenshot", Ebola.AntiScreenShot.block);